mapper.insert:
insert into s_user (user_name, tdate, ttimestamp
  )
values (#{userName,jdbcType=VARCHAR}, #{tdate,jdbcType=DATE}, #{ttimestamp,jdbcType=TIMESTAMP}
  )

mapper.update:
update s_user
set user_name = #{userName,jdbcType=VARCHAR},
  tdate = #{tdate,jdbcType=DATE},
  ttimestamp = #{ttimestamp,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, user_name, tdate, ttimestamp
from s_user
where id = #{id,jdbcType=VARCHAR}
